mapper.select:
select a.pageid, a.pagename, a.crtdate, a.updatedate , b.login_name "crtuser" from ${sysUser}.olap_user_save a, ${sysUser}.sc_login_user b where a.userid = b.user_id and a.userid = #{userId}
and a.pagename like '%${keyword}%'
order by a.crtdate desc

select c.table_id as "tid", c.table_name "tname", c.table_sql "sql", use_es "useEs" , es_enable "esEnable", data_control_col as "dataControlCol"
from ${sysUser}.etl_src_meta c 
where table_id = #{tid}

  	select col_id as "colId", col_name as "colName" , col_type as "colType", expression as "expression"
	from ${sysUser}.etl_src_col_meta where table_id = #{tid} 
	order by col_ord
